macro.command:
if (!game.modules.get("dfreds-convenient-effects")?.active) {
  ui.notifications.error("Please enable the CE module");
  return;
}

const lastArg = args[args.length - 1];

const DAEItem = lastArg.efData.flags.dae.itemData;

// console.warn("Details", {
//   args,
//   lastArg,
//   actor,
//   token,
//   DAEItem
// })

const EFFECT_LOOKUP = {
  "asleep": ["Unconscious"],
  "panicked": ["Frightened"],
  "sickened": ["Poisoned"],
};

async function eyebite(type, dc, targetActor) {
  const flavor = `${CONFIG.DND5E.abilities["wis"].label} DC${dc} ${DAEItem?.name || ""}`;
  const saveRoll = await targetActor.rollAbilitySave("wis", { flavor, fastForward: true });
  if (dc > saveRoll.total) {
    ChatMessage.create({ content: `${targetActor.name} failed the save with a ${saveRoll.total}` });
    const conditions = EFFECT_LOOKUP[type];
    conditions.forEach((condition) => {
      game.dfreds.effectInterface.addEffect({ effectName: condition, uuid: targetActor.uuid, origin: lastArg.origin });
    })
    DAE.setFlag(targetActor, "eyebiteSpell", { conditions, dc, origin: lastArg.origin });
  } else {
    ChatMessage.create({ content: `${targetActor.name} passed the save with a ${saveRoll.total}` });
  }
}

async function eyebiteDialog(userId, dc, targetActor) {
  const type = await DDBImporter.DialogHelper.AskUserButtonDialog(userId, {
    buttons: [
      { label: "Asleep", value: "asleep" },
      { label: "Panicked", value: "panicked" },
      { label: "Sickened", value: "sickened" },
    ],
    title: DAEItem.name ?? "Eyebite",
    content: `<p>Choose effect for ${DAEItem.name} target</p>`,
  },
  'column');

  if (!type) return;

  await eyebite(type, dc, targetActor);
}

async function getTarget(userId, ignoreInitial = false) {
  if (game.users.get(userId).targets.size === 1 && !ignoreInitial) {
    return game.users.get(userId).targets.toObject()[0];
  } else {
    const result = await DDBImporter.DialogHelper.AskUserButtonDialog(userId, {
      buttons: [
        { label: "I have targeted", value: true },
        { label: "Cancel", value: false },
      ],
      title: DAEItem.name ?? "Eyebite",
      content: `<p>Choose a single target for ${DAEItem.name}</p>`,
    },
    'column');
    if (result && game.users.get(userId).targets.size === 1) {
      return game.users.get(userId).targets.toObject()[0];
    } else if (result && getDamageImmunities.targets.size !== 0) {
      return getTarget(userId, ignoreInitial);
    } else {
      return undefined;
    }
  }
}

if (args[0] === "on") {
  const saveData = foundry.utils.deepClone(DAEItem.system.save);
  if (saveData.scaling === "spell") {
    const rollData = actor.getRollData();
    saveData.dc = rollData.attributes.spelldc;
  }

  const target = await getTarget(game.userId, false);

  if (!target) return;

  await eyebiteDialog(game.userId, saveData.dc, target.actor);
  ChatMessage.create({ content: `${target.name} is blessed with Eyebite` });

  const options = {
    userId: game.userId,
  };

  DAE.setFlag(actor, "eyebiteSpell", options);
} else if (args[0] === "each") {
  const flag = DAE.getFlag(actor, "eyebiteSpell");
  const userId = flag.userId ?? game.userId;

  const again = await DDBImporter.DialogHelper.AskUserButtonDialog(userId, {
    buttons: [
      { label: "Yes", value: true },
      { label: "No", value: false },
    ],
    title: DAEItem.name ?? "Eyebite",
    content: `<p>Would you like to use your action to use ${DAEItem.name} again?</p>`,
  },
  'column');

  if (!again) return;
  const target = await getTarget(userId, true);
  if (!target) return;

  await eyebiteDialog(userId, flag.dc, target.actor);
}

else if (args[0] === "off") {
  game.canvas.tokens.placeables.forEach((t) => {
    const effects = DDBImporter.EffectHelper.getActorEffects(t.actor)
      .filter((e => e.origin === lastArg.origin));
    if (effects.length > 0) {
      const flag = DAE.getFlag(t.actor, "eyebiteSpell") ?? { conditions: [] };
      flag.conditions.forEach((condition) => {
        if (DDBImporter.EffectHelper.isConditionEffectAppliedAndActive(condition, t.actor)) {
          game.dfreds.effectInterface.removeEffect({ effectName: condition, uuid: t.actor.uuid });
        }
      });
      DAE.unsetFlag(actor, "eyebiteSpell");
    }
  });
  await DAE.unsetFlag(actor, "eyebiteSpell");
}

if (!DDBImporter?.EffectHelper.checkJB2a(true, true, true)) return false;

const summonType = 'Dancing light';
const caster = game.actors.get(args[0].actor._id);
const spawnedIds = [];

// this is called when the item is created, you can run this manually if your actors are not created yet
// await DDBImporter.EffectHelper._createJB2aActors("Dancing Lights", "Dancing light");

const lightActors = game.actors.filter((f) =>f.name.includes(summonType));

const actorChoices = lightActors.map((actor) => {
  const labelSrc = actor.img.replace(/\\d+x\\d+\\.webm$/, 'Thumb.webp');
  const labelText = actor.name.split(' - ')[1];
  return {
    label: `<img src="${labelSrc}" width="40" height="40" style="border:0px"><br>${labelText}`,
    value: actor,
  };
});

async function preEffects(template, update) {
  //prep summoning area
  new Sequence()
    .effect()
    .atLocation(template)
    .file(getJB2aName(update.token.name))
    .center()
    .scale(1.5)
    .belowTokens()
    .play();
}

async function postEffects(template, token) {
  //bring in our minion
  new Sequence().animation().on(token).fadeIn(2000).play();
}

function getJB2aName(colorChoice) {
  if (game.modules.get('jb2a_patreon')?.active) {
    if (colorChoice.includes('Blue')) {
      return 'jb2a.markers.light.intro.blue';
    } else if (colorChoice.includes('Green')) {
      return 'jb2a.markers.light.intro.green';
    } else if (colorChoice.includes('Yellow')) {
      return 'jb2a.markers.light.intro.yellow02';
    } else {
      return 'jb2a.markers.light.intro.yellow';
    }
  } else {
    return 'jb2a.markers.light.intro.blue';
  }
}

for (let i = 0; i < 4; i++) {
  const choice = await warpgate.buttonDialog({
    title: 'Choose your color:',
    buttons: actorChoices,
  });

  if (!choice) break;

  const updates = {
    token: {
      name: `${choice.name} of ${caster.name}`,
      alpha: 0,
    },
    actor: {
      name: `${choice.name} of ${caster.name}`,
      system: {
        attributes: {
          hp: {
            value: 100,
            max: 100,
          },
        },
        details: {
          type: {
            custom: 'NoTarget',
            value: 'custom',
          },
        },
      },
    },
    flags: {
      'mid-qol': {
        neverTarget: true,
      }
    }
  };

  const callbacks = {
    pre: async (template, update) => {
      preEffects(template, update);
      await DDBImporter?.EffectHelper.wait(500);
    },
    post: async (template, token, updates) => {
      postEffects(template, token);
      await DDBImporter?.EffectHelper.wait(500);
      const sourceActorOrToken = fromUuidSync(
        updates.actor.flags.warpgate.control.actor
      );
      const sourceActor = sourceActorOrToken.actor ?? sourceActorOrToken;
      const concentrationFlag = sourceActor.getFlag('midi-qol', 'concentration-data');
      concentrationFlag.removeUuids.push(token.uuid);
      await sourceActor.setFlag('midi-qol', 'concentration-data', concentrationFlag);
    },
  };

  const options = { controllingActor: caster };
  const ids = await warpgate.spawn(
    await choice.getTokenDocument(),
    updates,
    callbacks,
    options,
  );

  if (!ids) break;
  spawnedIds.push(ids[0]);
}

await token.actor.setFlag('midi-qol', 'spawnedTokenIds', spawnedIds);
for (const id of spawnedIds) {
  const spawnedActor = canvas.scene.tokens.get(id).actor;
  await DAE.setFlag(spawnedActor, 'spawnedByTokenUuid', token.document.uuid);
}

const hookIdForSpawnedCreatures = Hooks.on('preDeleteToken', async (tokenDoc) => {
  const sourceTokenUuid = tokenDoc.actor.getFlag('dae', 'spawnedByTokenUuid');
  if (!sourceTokenUuid) return;
  new Sequence()
    .effect()
    .atLocation(tokenDoc.object.center)
    .file(`jb2a.smoke.puff.centered.grey.2`)
    .scale(tokenDoc.width / canvas.scene.grid.distance)
    .play();
  const sourceActor = fromUuidSync(sourceTokenUuid).actor;
  const spawnedIds = sourceActor.getFlag('midi-qol', 'spawnedTokenIds');
  if (!spawnedIds) return;
  const spawnedTokenDocs = [];
  for (const i of spawnedIds) {
    if (canvas.scene.tokens.get(i))
      spawnedTokenDocs.push(canvas.scene.tokens.get(i));
  }
  if (spawnedTokenDocs.length === 1) {
    await sourceActor.unsetFlag('midi-qol', 'spawnedTokenIds');
    Hooks.off('preDeleteToken', hookIdForSpawnedCreatures);
  }
});
