macro.command:
if (!game.modules.get("dfreds-convenient-effects")?.active) {
  ui.notifications.error("Please enable the CE module");
  return;
}

const lastArg = args[args.length - 1];

const DAEItem = lastArg.efData.flags.dae.itemData;

// console.warn("Details", {
//   args,
//   lastArg,
//   actor,
//   token,
//   DAEItem
// })

const EFFECT_LOOKUP = {
  "asleep": ["Unconscious"],
  "panicked": ["Frightened"],
  "sickened": ["Poisoned"],
};

async function eyebite(type, dc, targetActor) {
  const flavor = `${CONFIG.DND5E.abilities["wis"].label} DC${dc} ${DAEItem?.name || ""}`;
  const saveRoll = await targetActor.rollAbilitySave("wis", { flavor, fastForward: true });
  if (dc > saveRoll.total) {
    ChatMessage.create({ content: `${targetActor.name} failed the save with a ${saveRoll.total}` });
    const conditions = EFFECT_LOOKUP[type];
    conditions.forEach((condition) => {
      game.dfreds.effectInterface.addEffect({ effectName: condition, uuid: targetActor.uuid, origin: lastArg.origin });
    })
    DAE.setFlag(targetActor, "eyebiteSpell", { conditions, dc, origin: lastArg.origin });
  } else {
    ChatMessage.create({ content: `${targetActor.name} passed the save with a ${saveRoll.total}` });
  }
}

async function eyebiteDialog(userId, dc, targetActor) {
  const type = await DDBImporter.DialogHelper.AskUserButtonDialog(userId, {
    buttons: [
      { label: "Asleep", value: "asleep" },
      { label: "Panicked", value: "panicked" },
      { label: "Sickened", value: "sickened" },
    ],
    title: DAEItem.name ?? "Eyebite",
    content: `<p>Choose effect for ${DAEItem.name} target</p>`,
  },
  'column');

  if (!type) return;

  await eyebite(type, dc, targetActor);
}

async function getTarget(userId, ignoreInitial = false) {
  if (game.users.get(userId).targets.size === 1 && !ignoreInitial) {
    return game.users.get(userId).targets.toObject()[0];
  } else {
    const result = await DDBImporter.DialogHelper.AskUserButtonDialog(userId, {
      buttons: [
        { label: "I have targeted", value: true },
        { label: "Cancel", value: false },
      ],
      title: DAEItem.name ?? "Eyebite",
      content: `<p>Choose a single target for ${DAEItem.name}</p>`,
    },
    'column');
    if (result && game.users.get(userId).targets.size === 1) {
      return game.users.get(userId).targets.toObject()[0];
    } else if (result && getDamageImmunities.targets.size !== 0) {
      return getTarget(userId, ignoreInitial);
    } else {
      return undefined;
    }
  }
}

if (args[0] === "on") {
  const saveData = foundry.utils.deepClone(DAEItem.system.save);
  if (saveData.scaling === "spell") {
    const rollData = actor.getRollData();
    saveData.dc = rollData.attributes.spelldc;
  }

  const target = await getTarget(game.userId, false);

  if (!target) return;

  await eyebiteDialog(game.userId, saveData.dc, target.actor);
  ChatMessage.create({ content: `${target.name} is blessed with Eyebite` });

  const options = {
    userId: game.userId,
  };

  DAE.setFlag(actor, "eyebiteSpell", options);
} else if (args[0] === "each") {
  const flag = DAE.getFlag(actor, "eyebiteSpell");
  const userId = flag.userId ?? game.userId;

  const again = await DDBImporter.DialogHelper.AskUserButtonDialog(userId, {
    buttons: [
      { label: "Yes", value: true },
      { label: "No", value: false },
    ],
    title: DAEItem.name ?? "Eyebite",
    content: `<p>Would you like to use your action to use ${DAEItem.name} again?</p>`,
  },
  'column');

  if (!again) return;
  const target = await getTarget(userId, true);
  if (!target) return;

  await eyebiteDialog(userId, flag.dc, target.actor);
}

else if (args[0] === "off") {
  game.canvas.tokens.placeables.forEach((t) => {
    const effects = DDBImporter.EffectHelper.getActorEffects(t.actor)
      .filter((e => e.origin === lastArg.origin));
    if (effects.length > 0) {
      const flag = DAE.getFlag(t.actor, "eyebiteSpell") ?? { conditions: [] };
      flag.conditions.forEach((condition) => {
        if (DDBImporter.EffectHelper.isConditionEffectAppliedAndActive(condition, t.actor)) {
          game.dfreds.effectInterface.removeEffect({ effectName: condition, uuid: t.actor.uuid });
        }
      });
      DAE.unsetFlag(actor, "eyebiteSpell");
    }
  });
  await DAE.unsetFlag(actor, "eyebiteSpell");
}

// based on @ccjmk and @crymic macro for sleep. Gets targets and ignores those who are immune to sleep.
// uses convinient effects
// Midi-qol "On Use"

const sleepHp = await args[0].damageTotal;
const condition = "Unconscious";
console.log(`Sleep Spell => Available HP Pool [${sleepHp}] points`);
const targets = await args[0].targets
  .filter((i) => i.actor.system.attributes.hp.value != 0 && !i.actor.effects.find((x) => (x.name ?? x.label) === condition))
  .sort((a, b) => (canvas.tokens.get(a.id).actor.system.attributes.hp.value < canvas.tokens.get(b.id).actor.system.attributes.hp.value ? -1 : 1));
let remainingSleepHp = sleepHp;
let sleepTarget = [];

for (let target of targets) {
  const findTarget = await canvas.tokens.get(target.id);
  const targetRaceOrType = DDBImporter?.EffectHelper.getRaceOrType(findTarget.actor);
  const immuneType = ["undead", "construct", "elf", "half-elf"].some((race) => targetRaceOrType.includes(race));
  const immuneCI = findTarget.actor.system.traits.ci.custom.includes("Sleep");
  const sleeping = findTarget.actor.effects.find((i) => (i.name ?? i.label) === condition);
  const targetHpValue = findTarget.actor.system.attributes.hp.value;
  const targetImg = target?.texture?.src;
  if ((immuneType) || (immuneCI) || (sleeping)) {
    console.log(`Sleep Results => Target: ${findTarget.name} | HP: ${targetHpValue} | Status: Resists`);
    sleepTarget.push(`<div class="midi-qol-flex-container"><div>Resists</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${targetImg}" width="30" height="30" style="border:0px"></div></div>`);
  } else if (remainingSleepHp >= targetHpValue) {
    remainingSleepHp -= targetHpValue;
    console.log(`Sleep Results => Target: ${findTarget.name} |  HP: ${targetHpValue} | HP Pool: ${remainingSleepHp} | Status: Slept`);
    sleepTarget.push(`<div class="midi-qol-flex-container"><div>Slept</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${targetImg}" width="30" height="30" style="border:0px"></div></div>`);
    const gameRound = game.combat ? game.combat.round : 0;
    const effectData = {
      label: "Sleep Spell",
      name: "Sleep Spell",
      icon: "icons/svg/sleep.svg",
      origin: args[0].uuid,
      disabled: false,
      duration: { rounds: 10, seconds: 60, startRound: gameRound, startTime: game.time.worldTime },
      flags: { dae: { specialDuration: ["isDamaged"] } },
      changes: [
        DDBImporter.EffectHelper.generateCEStatusEffectChange(game.i18n.localize("Unconscious"), 20, true),
      ]
    };

    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: findTarget.actor.uuid, effects: [effectData] });
  } else {
    console.log(`Sleep Results => Target: ${target.name} | HP: ${targetHpValue} | HP Pool: ${remainingSleepHp - targetHpValue} | Status: Missed`);
    sleepTarget.push(`<div class="midi-qol-flex-container"><div>misses</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${targetImg}" width="30" height="30" style="border:0px"></div></div>`);
  }
}
await DDBImporter?.EffectHelper.wait(500);
const sleptResults = `<div><div class="midi-qol-nobox">${sleepTarget.join('')}</div></div>`;
const chatMessage = game.messages.get(args[0].itemCardId);
let content = foundry.utils.duplicate(chatMessage.content);
const searchString = /<div class="midi-qol-hits-display">[\s\S]*<div class="end-midi-qol-hits-display">/g;
const replaceString = `<div class="midi-qol-hits-display"><div class="end-midi-qol-hits-display">${sleptResults}`;
content = await content.replace(searchString, replaceString);
await chatMessage.update({ content: content });
