macro.command:
if (!DDBImporter?.EffectHelper.checkJB2a(true, true, true)) return false;

const summonType = 'Dancing light';
const caster = game.actors.get(args[0].actor._id);
const spawnedIds = [];

// this is called when the item is created, you can run this manually if your actors are not created yet
// await DDBImporter.EffectHelper._createJB2aActors("Dancing Lights", "Dancing light");

const lightActors = game.actors.filter((f) =>f.name.includes(summonType));

const actorChoices = lightActors.map((actor) => {
  const labelSrc = actor.img.replace(/\\d+x\\d+\\.webm$/, 'Thumb.webp');
  const labelText = actor.name.split(' - ')[1];
  return {
    label: `<img src="${labelSrc}" width="40" height="40" style="border:0px"><br>${labelText}`,
    value: actor,
  };
});

async function preEffects(template, update) {
  //prep summoning area
  new Sequence()
    .effect()
    .atLocation(template)
    .file(getJB2aName(update.token.name))
    .center()
    .scale(1.5)
    .belowTokens()
    .play();
}

async function postEffects(template, token) {
  //bring in our minion
  new Sequence().animation().on(token).fadeIn(2000).play();
}

function getJB2aName(colorChoice) {
  if (game.modules.get('jb2a_patreon')?.active) {
    if (colorChoice.includes('Blue')) {
      return 'jb2a.markers.light.intro.blue';
    } else if (colorChoice.includes('Green')) {
      return 'jb2a.markers.light.intro.green';
    } else if (colorChoice.includes('Yellow')) {
      return 'jb2a.markers.light.intro.yellow02';
    } else {
      return 'jb2a.markers.light.intro.yellow';
    }
  } else {
    return 'jb2a.markers.light.intro.blue';
  }
}

for (let i = 0; i < 4; i++) {
  const choice = await warpgate.buttonDialog({
    title: 'Choose your color:',
    buttons: actorChoices,
  });

  if (!choice) break;

  const updates = {
    token: {
      name: `${choice.name} of ${caster.name}`,
      alpha: 0,
    },
    actor: {
      name: `${choice.name} of ${caster.name}`,
      system: {
        attributes: {
          hp: {
            value: 100,
            max: 100,
          },
        },
        details: {
          type: {
            custom: 'NoTarget',
            value: 'custom',
          },
        },
      },
    },
    flags: {
      'mid-qol': {
        neverTarget: true,
      }
    }
  };

  const callbacks = {
    pre: async (template, update) => {
      preEffects(template, update);
      await DDBImporter?.EffectHelper.wait(500);
    },
    post: async (template, token, updates) => {
      postEffects(template, token);
      await DDBImporter?.EffectHelper.wait(500);
      const sourceActorOrToken = fromUuidSync(
        updates.actor.flags.warpgate.control.actor
      );
      const sourceActor = sourceActorOrToken.actor ?? sourceActorOrToken;
      const concentrationFlag = sourceActor.getFlag('midi-qol', 'concentration-data');
      concentrationFlag.removeUuids.push(token.uuid);
      await sourceActor.setFlag('midi-qol', 'concentration-data', concentrationFlag);
    },
  };

  const options = { controllingActor: caster };
  const ids = await warpgate.spawn(
    await choice.getTokenDocument(),
    updates,
    callbacks,
    options,
  );

  if (!ids) break;
  spawnedIds.push(ids[0]);
}

await token.actor.setFlag('midi-qol', 'spawnedTokenIds', spawnedIds);
for (const id of spawnedIds) {
  const spawnedActor = canvas.scene.tokens.get(id).actor;
  await DAE.setFlag(spawnedActor, 'spawnedByTokenUuid', token.document.uuid);
}

const hookIdForSpawnedCreatures = Hooks.on('preDeleteToken', async (tokenDoc) => {
  const sourceTokenUuid = tokenDoc.actor.getFlag('dae', 'spawnedByTokenUuid');
  if (!sourceTokenUuid) return;
  new Sequence()
    .effect()
    .atLocation(tokenDoc.object.center)
    .file(`jb2a.smoke.puff.centered.grey.2`)
    .scale(tokenDoc.width / canvas.scene.grid.distance)
    .play();
  const sourceActor = fromUuidSync(sourceTokenUuid).actor;
  const spawnedIds = sourceActor.getFlag('midi-qol', 'spawnedTokenIds');
  if (!spawnedIds) return;
  const spawnedTokenDocs = [];
  for (const i of spawnedIds) {
    if (canvas.scene.tokens.get(i))
      spawnedTokenDocs.push(canvas.scene.tokens.get(i));
  }
  if (spawnedTokenDocs.length === 1) {
    await sourceActor.unsetFlag('midi-qol', 'spawnedTokenIds');
    Hooks.off('preDeleteToken', hookIdForSpawnedCreatures);
  }
});

if (args[0].macroPass === "postSave" && workflow.failedSaves) {
  let targets = workflow.failedSaves;
  const commandWord = await globalThis.DDBImporter.DialogHelper.ChooserDialog.Ask(
    [
      {
        label: "What do you command?",
        type: "text",
        value: "",
        options: [""]
      }
    ],
    [
      {
        label: "Cast!",
        value: "1",
      }
    ],
    { title: "ðŸ—£Command Spell" }
  );

  for (let target of targets) {
    MidiQOL.socket().executeAsGM("_gmSetFlag", {
      actorUuid: target.actor.uuid,
      base: "midi-qol",
      key: "commandWord",
      value: commandWord.results[0],
    });
  }
}
if (args[0] === "off") {
  let wordInput = await actor.getFlag("midi-qol", "commandWord");
  let content = `${actor.name} must use their action to "${wordInput}".`;
  let actorPlayer = MidiQOL.playerForActor(actor);
  let chatData = {
    user: actorPlayer.id,
    speaker: ChatMessage.getSpeaker({ token: token }),
    content: content,
  };
  ChatMessage.create(chatData);
  MidiQOL.socket().executeAsGM("_gmUnsetFlag", { actorUuid: actor.uuid, base: "midi-qol", key: "commandWord" });
}

if (args[0] === "each") {
  const findEffect = token.actor.effects.find((effect) => effect.name === "Confusion");

  if (findEffect) {
    const changeIndex = findEffect.changes.findIndex(
      (change) =>
        change.key === "system.attributes.movement.all" &&
        change.mode === CONST.ACTIVE_EFFECT_MODES.CUSTOM &&
        change.value === 0
    );

    if (changeIndex !== -1) {
      findEffect.changes.splice(changeIndex, 1);

      await MidiQOL.socket().executeAsGM("updateEffects", {
        actorUuid: token.actor.uuid,
        updates: [{ _id: findEffect.id, changes: findEffect.changes }],
      });
    } else {
      console.log("Specified change not found in the Confusion effect.");
    }
  } else {
    console.log("Confusion effect not found on the actor.");
  }

  const hasEffectApplied = await game.dfreds.effectInterface.hasEffectApplied("Reaction", token.actor.uuid);
  if (!hasEffectApplied) {
    game.dfreds.effectInterface.addEffect({ effectName: "Reaction", uuid: token.actor.uuid });
  }

  const confusionRoll = await new CONFIG.Dice.DamageRoll("1d10").evaluate({ async: true });
  await MidiQOL.displayDSNForRoll(confusionRoll, "damageRoll");
  const result = confusionRoll.total;
  let content;
  let selectedTokenMessage;
  let directionResult;
  let directionContent;
  switch (result) {
    case 1: {
      content = "The creature uses all its movement to move in a random direction. To determine the direction, roll a d8 and assign a direction to each die face. The creature doesn't take an action this turn.";
      const directionRoll = await new CONFIG.Dice.DamageRoll("1d8").evaluate({ async: true });
      await MidiQOL.displayDSNForRoll(directionRoll, "damageRoll");
      directionResult = directionRoll.total;
      directionContent = null;
      switch (directionResult) {
        case 1:
          directionContent = "North";
          break;
        case 2:
          directionContent = "South";
          break;
        case 3:
          directionContent = "East";
          break;
        case 4:
          directionContent = "West";
          break;
        case 5:
          directionContent = "Northwest";
          break;
        case 6:
          directionContent = "Northeast";
          break;
        case 7:
          directionContent = "Southwest";
          break;
        case 8:
          directionContent = "Southeast";
          break;
      }
      break;
    }
    case 2:
    case 3:
    case 4:
    case 5:
    case 6: {
      content = "The creature doesn't move or take actions this turn.";
      const findEffect = token.actor.effects.find((effect) => effect.name === "Confusion");
      if (findEffect)
        findEffect.changes.push({
          key: "system.attributes.movement.all",
          mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
          value: 0,
          priority: 20,
        });
      await MidiQOL.socket().executeAsGM("updateEffects", {
        actorUuid: token.actor.uuid,
        updates: [{ _id: findEffect.id, changes: findEffect.changes }],
      });
      break;
    }
    case 7:
    case 8: {
      content = "The creature uses its action to make a melee attack against a randomly determined creature within its reach. If there is no creature within its reach, the creature does nothing this turn.";
      const rangeCheck = MidiQOL.findNearby(null, token.actor, token.actor.system.attributes.movement.walk, {
        includeToken: false,
      });
      if (rangeCheck.length > 0) {
        const randomSelection = rangeCheck[Math.floor(Math.random() * rangeCheck.length)];
        selectedTokenMessage = `The creature must move to ${randomSelection.actor.name} and attack them with a melee attack.`;
        if (game.modules.get("sequencer")?.active) {
          const target = randomSelection;
          new Sequence()
            .effect()
            .from(target)
            .belowTokens()
            .attachTo(target, { locale: true })
            .scaleToObject(1, { considerTokenScale: true })
            .spriteRotation(target.rotation * -1)
            .filter("Glow", { color: 0x911a1a, distance: 15 })
            .duration(30000)
            .fadeIn(2000, { delay: 1000 })
            .fadeOut(3500, { ease: "easeInSine" })
            .opacity(0.8)
            .zIndex(0.1)
            .loopProperty("alphaFilter", "alpha", { values: [0.5, 0], duration: 1000, pingPong: true, delay: 500 })
            .effect()
            .file("jb2a.extras.tmfx.outflow.circle.01")
            .attachTo(target, { locale: true })
            .scaleToObject(3, { considerTokenScale: false })
            .randomRotation()
            .duration(30000)
            .fadeIn(5000, { delay: 1000 })
            .fadeOut(3500, { ease: "easeInSine" })
            .scaleIn(0, 3500, { ease: "easeInOutCubic" })
            .tint(0x870101)
            .opacity(0.5)
            .belowTokens()
            .play();
        }
      } else {
        selectedTokenMessage = `The creature does nothing this turn.`;
      }
      break;
    }
    case 9:
    case 10:
      content = "The creature can act and move normally.";
      break;
  }
  ChatMessage.create({ content: `Confusion roll for ${token.actor.name} is ${result}:<br> ` + content });
  if (result === 1)
    ChatMessage.create({
      content: `Movement roll for ${token.actor.name} is ${directionResult}: ${token.actor.name} must move ${directionContent} using all (${token.actor.system.attributes.movement.walk} feet) of their movement.`,
    });
  if (result === 7 || result === 8) ChatMessage.create({ content: selectedTokenMessage });
}

if (args[0] === "off") {
  const findEffect = token.actor.effects.find((effect) => effect.name === "Confusion");

  if (findEffect) {
    const changeIndex = findEffect.changes.findIndex(
      (change) =>
        change.key === "system.attributes.movement.all" &&
        change.mode === CONST.ACTIVE_EFFECT_MODES.CUSTOM &&
        change.value === 0
    );

    if (changeIndex !== -1) {
      findEffect.changes.splice(changeIndex, 1);

      await MidiQOL.socket().executeAsGM("updateEffects", {
        actorUuid: token.actor.uuid,
        updates: [{ _id: findEffect.id, changes: findEffect.changes }],
      });
    } else {
      console.log("Specified change not found in the Confusion effect.");
    }
  } else {
    console.log("Confusion effect not found on the actor.");
  }
}
